CREATE DATABASE IF NOT EXISTS `boerick_conv` DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci;
USE `boerick_conv`;

CREATE TABLE boerick_conv(
   no_items    INTEGER  NOT NULL PRIMARY KEY 
  ,no_meds     INTEGER  NOT NULL
  ,book_name   VARCHAR(8) NOT NULL
  ,med_name    VARCHAR(41) NOT NULL
  ,section     VARCHAR(22) NOT NULL
  ,med_details VARCHAR(3856) NOT NULL
);
